mapper.select:
        SELECT  count(*)
        FROM article
        WHERE 1 =1
        
            and  head LIKE CONCAT('%',#{head},'%')
        
        
            and  subject = #{subject}
        
        
            and  type = #{type}
        
        
            and  user_id = #{user_id}
        
        
            and  (examine_status = 1 or examine_status = 0)
        
        
            and  examine_status = 1
        

        and deleted = 0

        SELECT *
        FROM article

        WHERE 1 =1
        
            and  head LIKE CONCAT('%',#{head},'%')
        
        
            and  subject = #{subject}
        
        
            and  type = #{type}
        
        
            and  user_id = #{user_id}
        
        
            and  (examine_status = 1 or examine_status = 0)
        
        
            and  examine_status = 1
        
        and deleted = 0
        ORDER BY create_time DESC
        limit #{begin},#{end}
