mapper.select:
        SELECT  count(*)
        FROM video
        WHERE 1 =1
        
            and  title LIKE CONCAT('%',#{title},'%')
        

        
            and  examine_status =  #{status}
        

        
            and  subject IN
            
                #{id}
            
        

        
            and  watch_type IN
            
                #{id}
            
        
        and deleted = 0

        SELECT *
        FROM video

        WHERE 1 =1
        
            and  title LIKE CONCAT('%',#{title},'%')
        

        
            and  examine_status =  #{status}
        

        
            and  subject IN
            
                #{id}
            
        

        
            and  watch_type IN
            
                #{id}
            
        
        
            ORDER BY praise_count DESC
        
        
            ORDER BY play_count DESC
        
        
            ORDER BY create_time DESC
        
        limit #{begin},#{end}
