mapper.select:
        SELECT  count(*)
        FROM video
        WHERE 1 =1
        
            and  title LIKE CONCAT('%',#{title},'%')
        

        
            and  examine_status =  #{status}
        

        
            and  id =  #{video_id}
        

        
            and  user_id =  #{user_id}
        

        
            
                and  pools = ""
            
            
                and  pools LIKE CONCAT('%',#{pools},'%')
            
        


        
            and  subject IN
            
                #{id}
            
        

        
            and  watch_type IN
            
                #{id}
            
        

        
            and  user_id IN
            
                #{id}
            
        


        and deleted = 0
